-- CREATE TABLE "stg_borrower" ("borrower_id" VARCHAR(36), "full_name" VARCHAR(50), "street" VARCHAR(100), "city" VARCHAR(100), "state" VARCHAR(50), "zip_code" VARCHAR(15), "phone_home" VARCHAR(12), "phone_cell" VARCHAR(12), email VARCHAR(128));
-- DROP TABLE IF EXISTS stg_role_profile;
